const webpack = require('webpack')
const library = '[name]_lib'
const path = require('path')

module.exports = {
    entry: {
        ngcore: ['@angular/core']
    },
    output: {
        filename: '[name].dll.js',
        path: 'dist/libs/',
        library
    },
    plugins: [
        new webpack.DllPlugin({
            path: path.join(__dirname, 'dist/[name]-manifest.json'),
            name: library
        }),
        new webpack.optimize.UglifyJsPlugin({
            compress: {
                warnings: false
            }
        })
    ],
}

